contracts/core/Adminable.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.7.4;

import './AdminStorage.sol';

contract Adminable {
    AdminStorage public adminStorage;

    constructor(address _store) {
        require(_store != address(0), "new storage shouldn't be empty");
        adminStorage = AdminStorage(_store);
    }

    modifier onlyAdmin() {
        require(adminStorage.isAdmin(msg.sender), 'Not admin');
        _;
    }

    function isAdmin(address from) external view returns (bool) {
        return adminStorage.isAdmin(from);
    }

    function admin() external view returns (address) {
        return adminStorage.admin();
    }

    function getAdminStorage() internal view returns (AdminStorage) {
        return adminStorage;
    }
}

contracts/core/AdminStorage.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.7.4;

import 'hardhat/console.sol';

contract AdminStorage {
    address public admin;
    address public pendingAdmin;

    event NewPendingAdmin(address oldPendingAdmin, address newPendingAdmin);

    event NewAdmin(address oldAdmin, address newAdmin);

    constructor(address _admin) {
        admin = _admin;
        emit NewAdmin(address(0), admin);
    }

    modifier onlyAdmin() {
        require(isAdmin(msg.sender), 'Not admin');
        _;
    }

    modifier onlyPendingAdmin() {
        require(isPendingAdmin(msg.sender), 'Not pending admin');
        _;
    }

    function setPendingAdmin(address admin_) external onlyAdmin {
        require(admin_ != address(0), "new admin shouldn't be empty");
        require(admin_ != admin, 'new admin is the save with old admin');
        address oldPendingAdmin = pendingAdmin;
        pendingAdmin = admin_;
        emit NewPendingAdmin(oldPendingAdmin, pendingAdmin);
    }

    function acceptAdmin() external onlyPendingAdmin {
        address oldAdmin = admin;
        admin = pendingAdmin;
        emit NewAdmin(oldAdmin, admin);
        address oldPendingAdmin = pendingAdmin;
        pendingAdmin = address(0);
        emit NewPendingAdmin(oldPendingAdmin, pendingAdmin);
    }

    function isAdmin(address account) public view returns (bool) {
        return account == admin;
    }

    function isPendingAdmin(address account) public view returns (bool) {
        return account == pendingAdmin;
    }
}

